/*Global variables -----------------------------------------------*/
uint8_t txbuffer[128];
uint8_t rxbuffer[128];
uint8_t _txbuffer[128];
uint8_t _rxbuffer[128];
/*Function prototypes --------------------------------------------*/

void suma(int length, ...);

int main(void)
{
	RCC->AHB1ENR |= GPIOX_CLOCK(LED);
	GPIOX_MODER(MODE_OUT, LED);
	//delay init
	delay_init_it();
	//UART2 PINOUT
	RCC->AHB1ENR |= GPIOX_CLOCK(UART2_TX);
	GPIOX_MODER(MODE_ALTER, UART2_TX);
	GPIOX_MODER(MODE_ALTER, UART2_RX);
	GPIOX_AFR(7U, UART2_TX);
	GPIOX_AFR(7U, UART2_RX);
	//UART2
	RCC->APB1ENR |= RCC_APB1ENR_USART2EN;
	//Deshabilitar
	USART2->CR1 = 0;
	USART2->CR1 |= USART_CR1_TE | USART_CR1_RE; //habilita la recepcion y la transmision
	/**
	 * baud = 115200
	 * USARTDIV  =16MHz/(16 * 115200)
	 * USARTDIV = 8.6805
	 * DIV_Mantisa = 0x8
	 * DIV_Fraction = 16 * 0.6806 ~  = B
	 * BRR = DIV_Mantisa<<4 | DIV_Fraction
	 * BRR = 0x8B
	 */
	USART2->BRR = 0x8B;
	USART2->CR1 |= USART_CR1_UE;
	//CONFIGURACIN DE LA IT
	/*local*/
	USART2->CR1 |= USART_CR1_RXNEIE;		//habilita la IT para la recepciÃ³n
	/*global*/
	NVIC_EnableIRQ(USART2_IRQn);			//Habilita la IT en la NVIC
	NVIC_SetPriority(USART2_IRQn, 2);		//cambia la prioridad
	//todo
	SERIAL_PRINTLN("CONFIGURACION CORRECTA");
	CircularUART2_Init();
	CircularUART_StartTx(txbuffer, 6);

	sprintf((char*)_txbuffer, "hola mundo desde buffer circular\r\n");
	CircularUART_Send(_txbuffer, strlen((char*)txbuffer));
	CircularUART_StartRx(rxbuffer, 7);

	CircularUART_Receive(_rxbuffer, 7);
    /* Loop forever */

	for(;;){

	}
}
