int __io_putchar(int ch){
#if (USE_SW0 == 1)
	ITM_SendChar(ch);
#else
	//todo
	uint8_t c = ch & 0xFF;
	while(!(USART2->SR & USART_SR_TXE));
	USART2->DR = c;
#endif
	return ch;
}
#if USE_SW0 == 0
int __io_getchar(void){
	while(!(USART2->SR & USART_SR_RXNE));
	return USART2->DR;
}
#endif
